<?phpclass ControllerProductFntCustomerDesign extends Controller {    public function index() {        $this->load->language('product/fnt_customer_design');		$this->document->addStyle('catalog/view/theme/'.$this->config->get('config_template').'/stylesheet/fancy_design/fancy-product.css');        $this->document->setTitle($this->language->get('heading_title'));        $data['breadcrumbs']   = array();        $data['breadcrumbs'][] = array(            'text' => $this->language->get('text_home'),            'href' => $this->url->link('common/home'),            'separator' => false        );        $data['text_customize']  = $this->language->get('text_customize');        $data['text_tax']      = $this->language->get('text_tax');        $url                   = '';        $data['compare']       = $this->url->link('product/compare');        if (isset($this->request->get['limit'])) {            $url .= '&limit=' . $this->request->get['limit'];        }        if (isset($this->request->get['sort'])) {            $url .= '&sort=' . $this->request->get['sort'];        }        if (isset($this->request->get['order'])) {            $url .= '&order=' . $this->request->get['order'];        }        if (isset($this->request->get['page'])) {            $url .= '&page=' . $this->request->get['page'];        }        $data['breadcrumbs'][] = array(            'text' => 'Настройки дизайна',            'href' => $this->url->link('product/fnt_customer_design'),            'separator' => ''        );        $data['heading_title'] = $this->language->get('heading_title');        $data['text_sort']     = $this->language->get('text_sort');        $data['text_limit']    = $this->language->get('text_limit');        if (isset($this->request->get['page'])) {            $page = $this->request->get['page'];        } else {            $page = 1;        }        if (isset($this->request->get['sort'])) {            $sort = $this->request->get['sort'];        } else {            $sort = 'p.date_added';        }        if (isset($this->request->get['order'])) {            $order = $this->request->get['order'];        } else {            $order = 'DESC';        }        if (isset($this->request->get['limit'])) {            $limit = $this->request->get['limit'];        } else {            $limit = '32';        }        $this->load->model('catalog/fnt_customer_design');		$data['products']        = array();        $product_total = $this->model_catalog_fnt_customer_design->getTotalProduct();        if ($product_total) {            $url = '';            $this->load->model('catalog/review');            $this->load->model('tool/image');            $data['button_cart']     = $this->language->get('button_cart');            $data['button_wishlist'] = $this->language->get('button_wishlist');            $data['button_compare']  = $this->language->get('button_compare');            $data['button_continue'] = $this->language->get('button_continue');            $filter_data             = array(                'sort' => $sort,                'order' => $order,                'start' => ($page - 1) * $limit,                'limit' => $limit            );            $results                 = $this->model_catalog_fnt_customer_design->getAllProductCustomerDesign();            foreach ($results as $result) {                if ($result['image']) {                    $image = $result['image'];                } else {                    $image = 'no_image.png';                }                if (($this->config->get('config_customer_price') && $this->customer->isLogged()) || !$this->config->get('config_customer_price')) {                    $price = $this->currency->format($this->tax->calculate($result['price'], $result['tax_class_id'], $this->config->get('config_tax')));                } else {                    $price = false;                }                if ($this->config->get('config_review')) {                    $rating = $this->model_catalog_review->getAverageRating($result['product_id']);                } else {                    $rating = false;                }                /*check price special*/                $special_price = $this->model_catalog_fnt_customer_design->getProductSpecial($result['product_id']);                if ((float) $special_price) {                    $special = $this->currency->format($this->tax->calculate((float) $special_price, $result['tax_class_id'], $this->config->get('config_tax')));                } else {                    $special = false;                }                //end check                if ($this->config->get('config_tax')) {                    $tax = $this->currency->format((float) $special_price ? $special_price : $result['price']);                } else {                    $tax = false;                }                if ($result['product_customer_idea_accept_id']) {                    $design = $this->url->link('product/fnt_category_product_design', 'product_customer_idea_accept_id=' . $result['product_customer_idea_accept_id']);                } else {                    $design = false;                }                $data['products'][] = array(                    'name' => $result['name'],                    'design' => $design,                    'product_id' => $result['product_id'],                    'thumb' => (class_exists('HelperImage')) ? HelperImage::resize($image, $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height')) : (class_exists('ModelToolImage')) ? $this->model_tool_image->resize($image, $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height')) : image_resize($image, $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height')),                    'price' => $this->currency->format($this->tax->calculate($result['price'], $result['tax_class_id'], $this->config->get('config_tax'))),                    'special' => $special,                    'tax' => $tax,                    'href' => $design                );            }            if (!$this->config->get('config_customer_price')) {                $data['display_price'] = TRUE;            } elseif ($this->customer->isLogged()) {                $data['display_price'] = TRUE;            } else {                $data['display_price'] = FALSE;            }            $url = '';            if (isset($this->request->get['limit'])) {                $url = '&limit=' . $this->request->get['limit'];            }            $data['sorts']    = array();            $data['sorts'][]  = array(                'text' => $this->language->get('text_newest'),                'value' => 'p.date_available-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.date_available&order=DESC')            );            $data['sorts'][]  = array(                'text' => $this->language->get('text_name_asc'),                'value' => 'pd.name',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=pd.name')            );            $data['sorts'][]  = array(                'text' => $this->language->get('text_name_desc'),                'value' => 'pd.name-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=pd.name&order=DESC')            );            $data['sorts'][]  = array(                'text' => $this->language->get('text_price_asc'),                'value' => 'p.price-ASC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.price&order=ASC')            );            $data['sorts'][]  = array(                'text' => $this->language->get('text_price_desc'),                'value' => 'p.price-DESC',                'href' => (HTTP_SERVER . 'index.php?route=product/fnt_customer_design' . $url . '&sort=p.price&order=DESC')            );            $data['sorts']